        for (int i = 0; i < BallNum; i++) {
            int RNumber = 0;
            for (int j = 0; j < SlotsNum - 1; j++) {
                if (SlotsTrace[i][j] == "R") {
                    RNumber += 1;
                }
            }
            BallResult[i] = RNumber;
        }

    }


    public void DrawSlots() {
        //insert the BallResult into the ArrangeTrace
        for (int i = 0; i < BallNum; i++) {
