        for (int i = 0; i < SlotsNum; i++) {
            for (int j = 0, t = BallNum - 1; j < BallResultList[1][i]; j++, t--) {
                ArrangedTrace[t][i-1] = "o";
            }
        }


        //arranged
        for (int j = 0; j < BallNum; j++) {
