public class EightQueen {
    EightQueen(){}

    //start from 1-8 without 0, getting a random Array
    private int[] GetEightRandomArray(){
        Random random = new Random();
        int[] Result = new int[8];
        for(int i=0; i < 8; i++){
            int Unique = random.nextInt(9);
            while(CheckContain(Unique, Result)){
                Unique = random.nextInt(9);
            }
            Result[i] = Unique;
        }
        return Result;
    }

    private Boolean CheckContain(int num, int[] Array){
        Boolean Result = Boolean.FALSE;
        for(int i=0; i < Array.length; i++){
            if(num == Array[i]){
                Result = Boolean.TRUE;
            }
        }
        return Result;
    }

    private String[][] TwoDimensionArray(){
        String[][] Result = new String[8][8];
        int[] InsertPoi = GetEightRandomArray();
        for(int i=0; i < 8; i++){
            int poi = InsertPoi[i] - 1;
            for(int j=0; j < 8; j++){
                if(j == poi){
                    Result[i][j] = "Q";
                }
                else{
                    Result[i][j] = " ";
                }
            }
        }
        return Result;
    }

    public void PrintDiagram(){
